mapper.insert:
INSERT INTO totalreview VALUES(
 (SELECT NVL(MAX(trno)+1,1) FROM totalreview),
 #{tno},#{trating},#{id},#{nickname},#{tcontent},SYSDATE,#{type}
)

mapper.update:
UPDATE totalreview SET
tcontent=#{tcontent}
WHERE trno=#{trno}

mapper.select:
SELECT COUNT(*)
FROM totalreview
WHERE id = #{id}
AND tno = #{tno}
